package day7program;

public class DoWhileLoop3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 1, times = 0;
		do {
			System.out.println("java do while loops:" + n);
			n++;
		} while (n <= times);
		while (n <= times) {
			System.out.println("java while loops:" + n);
			n++;
		}
	}

}
